import React , {useRef} from 'react';

import Container from '@material-ui/core/Container';
import SearchIcon from "@material-ui/icons/Search";
import TextField from "@material-ui/core/TextField";
import { fade, makeStyles } from "@material-ui/core/styles";

// import {Switch, Route} from 'react-router-dom';

import Header from './header';
import {GET_Request} from './request';



const useStyles = makeStyles((theme) => ({

  search: {
    position: "relative",
    borderRadius: theme.shape.borderRadius,
    backgroundColor: fade(theme.palette.common.white, 0.15),
    "&:hover": {
      backgroundColor: fade(theme.palette.common.white, 0.25)
    },
  },

  searchIcon: {
    padding: theme.spacing(0, 2),
    height: "100%",
    position: "absolute",
    pointerEvents: "none",
    display: "flex",
    alignItems: "center",
    justifyContent: "center"
  },

  inputRoot: {
    color: "inherit"
  },
  inputInput: {
    padding: theme.spacing(1, 1, 1, 0),
    paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
    transition: theme.transitions.create("width"),
    width: "100%",
    [theme.breakpoints.up("md")]: {
      width: "20ch"
    }
  },
}));

function search ({target}) {
  console.dir(target)

  // GET_Request()
  // .then (({data}) => console.log(data) )
  // .catch(err => console.log("sadsad" +err))
}


function News() {
  // <InputLabel >Email address</InputLabel>
  const searchBox = useRef(null);

    function handleClick() {
      console.log(searchBox.current)
      searchBox.current.focus();
    }
  const classes = useStyles();
  return (
    <>
      <Header/>
      <Container maxWidth = "sm" 
      style ={{ margin: "100px 0px 0px 0px" , background : ""}}
      onClick = {handleClick}>
      <div className={classes.search} style = {{margin : "auto", border: "1px black solid"}}>
          <div className={classes.searchIcon}>
            <SearchIcon 
              onChange = {search} />
          </div>
          <TextField
             style = {{border: "1px black solid", background :"red"}}
             ref={searchBox}
            placeholder="Search...."
            classes={{
              root: classes.inputRoot,
              input: classes.inputInput
            }}
          />
        </div>
      </Container>
    </>
  );
}

export default News;
