import React , {useState, useEffect} from 'react';
import SearchBar from "material-ui-search-bar";

import Container from '@material-ui/core/Container';


// import {Switch, Route} from 'react-router-dom';
import Header from './header';
import MediaCard from './mediaCard';
import {GET_Request} from './request';



function News() {

  const [query, changeQuery] = useState("")
  const [articles, changeArticles] = useState({})

  function search () {
    let queryString = query.trim()
    queryString = queryString.split(' ').join('+');
    GET_Request(queryString)
    .then (({data}) => fillMediaCards(data.articles) )
    .catch(err => console.log(err))
  }

  function fillMediaCards (data) {
    console.dir(data)
    let cards = <MediaCard/>
    changeArticles(cards)

  }


  function change (newQuery) {
    changeQuery(newQuery);
  }

  return (
    <>
      <Header/>
      <Container maxWidth = "md" 
      style ={{ margin: "100px 0px 0px 0px" , background : "grey", padding:"20px"}}>
      <SearchBar
        value={query}
        placeholder = "Search....."
        onChange={(newQuery) => change( newQuery )}
        onRequestSearch={search}
      />
      </Container >
      <div style = {{padding : "30px", margin : "auto" , width : "100%", 
        background : "red"}}>
        {articles ? articles  : ""}
      </div>
    </>
  );
}

export default News;
